import styled from 'styled-components'

const StyledUserBlock = styled.div`
	display: flex;
	max-width: 200px;
	align-items: center;
	height: 35px;
	margin-top: 23px;
	cursor: pointer;

	i {
		display: block;
	}
	.ant-dropdown-trigger.ant-dropdown-open {
	  i {
	      transform: rotate(180deg);
	  }
	}
`

export default StyledUserBlock
