import styled from 'styled-components'

const StyledMenuItem = styled.div`
	// padding-left: 50px;
	font-family: var(--fontGilroyMedium);
	font-size: 15px;
	line-height: 20px;
	letter-spacing: 0.16px;
	color: var(--white);
	&:hover {
		color: var(--orange);
		text-underline: none !important;
		a {
			color: var(--orange);
		}
	}
	a {
		color: var(--white);

		&:hover {
			 color: var(--orange);
		}
	}
`

export default StyledMenuItem
