import React from 'react'
import styled from 'styled-components'
import { DEVICE } from '../../constants/media'
import MenuHeader from '../MenuHeader/MenuHeader'
import EnvelopeIcon from '../../assets/img/envelope.svg'

import Logo from './components/Logo/Logo'
import LangControl from './components/LangControl/LangControl'
import UserBlock from './components/UserBlock/UserBlock'

const Header = () => {
  return (
    <StyledHeader>
      <Container>
        <StyledHeaderWrap>
          <Logo />

          <StyledHeaderRightCol>
            <MenuHeader />
            <UserActions>
              <LangControl />
              <UserBlock />
            </UserActions>
            <MobileActions>
              <i><img src={EnvelopeIcon} alt='' /></i>

            </MobileActions>
          </StyledHeaderRightCol>

        </StyledHeaderWrap>
      </Container>
    </StyledHeader>
  )
}
export default Header

const StyledHeader = styled.div`
  display: flex;
  background: var(--gradientHeader);
  height: 103px;
`

const Container = styled.div`
  max-width: 1250px;
  display: ${({ display }) => display === "flex" ? "flex" : "block"}};
  ${({ justify }) => justify && `justify-content: ${justify};`}
  width: 100%;
  padding: 0 1.5rem;
  margin: 0 auto;

	@media ${DEVICE.laptopS} {
    max-width:1200px;
  }

  @media ${DEVICE.laptopL}  {
    max-width: 1250px;
  }

  @media ${DEVICE.desktop} {
    max-width: 1450px;
  }
`

const StyledHeaderWrap = styled.div`
  	display: flex;
  	justify-content: space-between;
`

const StyledHeaderRightCol = styled.div`
    display: flex;
    align-items: center;
    min-width: 50%;
`

const UserActions = styled.div`
  display: flex;
  flex: 2;
  align-items: center;
  @media ${DEVICE.tabletL} {
    display: none;
  }
`;

const MobileActions = styled.div``;
