import React from 'react'
import styled from 'styled-components'
import { DEVICE } from '../../../constants/media'
import MenuHeader from '../MenuHeader/MenuHeader'

import Logo from './components/Logo/Logo'
import StyledHeaderWrap from './styled/StyledHeaderWrap'
import LangControl from './components/LangControl/LangControl'
import UserBlock from './components/UserBlock/UserBlock'
import StyledHeaderRightCol from './styled/StyledHeaderRightCol'

const Header = () => {
  return (
    <StyledHeader>
      <Container>
        <StyledHeaderWrap>
          <Logo />

          <StyledHeaderRightCol>
            <MenuHeader />
            <LangControl />
            <UserBlock />
          </StyledHeaderRightCol>

        </StyledHeaderWrap>
      </Container>
    </StyledHeader>
	    )
}
export default Header

const StyledHeader = styled.div`
  	display: flex;
  	background: var(--gradientHeader);
    height: 103px;

	@media ${DEVICE.tabletDevices1200} {

	}
`

const Container = styled.div`
  max-width: 1250px;
  display: ${({ display }) => display === "flex" ? "flex" : "block"}};
  ${({ justify }) => justify && `ustify-content: ${justify};`}
  width: 100%;
  padding-right: 15px;
  padding-left: 15px;
  margin-right: auto;
  margin-left: auto;

	 @media (min-width: 576px) {
	       max-width:540px;
	}

	@media (min-width: 768px) {
	        max-width:720px
	}

	@media (min-width: 992px) {
	        max-width:960px;
	}

	@media (min-width: 1200px) {
        max-width:1200px;
    }

    @media (min-width: 1300px) {
        max-width: 1250px;
    }

     @media (min-width: 1800px) {
        max-width: 1450px;
    }

`
