import React from 'react'
import styled from 'styled-components'
import { DEVICE } from '../../constants/media'
import MenuHeader from '../MenuHeader/MenuHeader'

import Logo from './components/Logo/Logo'
import LangControl from './components/LangControl/LangControl'
import UserBlock from './components/UserBlock/UserBlock'
import StyledHeaderRightCol from './styled/StyledHeaderRightCol'

const Header = () => {
  return (
    <StyledHeader>
      <Container>
        <StyledHeaderWrap>
          <Logo />

          <StyledHeaderRightCol>
            <MenuHeader />
            <LangControl />
            <UserBlock />
          </StyledHeaderRightCol>

        </StyledHeaderWrap>
      </Container>
    </StyledHeader>
  )
}
export default Header

const StyledHeader = styled.div`
  display: flex;
  background: var(--gradientHeader);
  height: 103px;
`

const Container = styled.div`
  // max-width: 1250px;
  display: ${({ display }) => display === "flex" ? "flex" : "block"}};
  ${({ justify }) => justify && `justify-content: ${justify};`}
  width: 100%;
  padding-right: 15px;
  padding-left: 15px;
  margin: 0 auto;

	// @media ${DEVICE.laptopS} {
  //   max-width:1200px;
  // }

  // @media ${DEVICE.laptopL}  {
  //   max-width: 1250px;
  // }

  // @media ${DEVICE.desktop} {
  //   max-width: 1450px;
  // }
`

const StyledHeaderWrap = styled.div`
  	display: flex;
  	justify-content: space-between;
`
